                        }                                                                   # End else (if (!$NicObject -or !$SubnetObject -or !$VnetObject))
                    }                                                                       # End elseif ($OperatorSelect -eq '1')
                    elseif ($OperatorSelect -eq '2') {                                      # Else if $OperatorSelect equals '1'
